var alertBoxs=function(imgUrl,title) {
    var html = `
                <div class="alertBoxs">
                    <div class="alerts">
                        <em class="remo"></em>
                        <h6>${title}</h6>
                        <img src="${imgUrl}">
                        <button type="button" class="btn-alerts">我知道了</button>
                    </div>
	            </div>`;
    $('body').append(html);
    $('.alertBoxs').on('click', '.remo', function () {
        $('.alertBoxs').remove();
    });
    $('.alertBoxs').on('click', '.btn-alerts', function () {
        $('.alertBoxs').remove();
    });
};